import { Grid, Paper, List, ListItem, ListItemText, Divider, Button } from "@mui/material";
import { withStyles } from "@mui/styles";
import PropTypes from "prop-types";
import { Fragment, useState } from "react";
import { useQuery } from "react-query";
import ContactForm from "./ContactForm";


const styles ={
  paper: {
    margin: "24px",
    padding: "16px" 
  }
}

const Contacts = ({ classes, setContactId }) => {
  const contact = useQuery("/contacts");
  const { isFetching, isFetched, error, data } = contact;
  const [editingId, setEditingId] = useState(null);

  return (
    <Paper className={classes.paper}>
      <div>List of contacts</div>

      { isFetching && <h1>Loading...</h1> }

      { isFetched && error && <h1>{data?.message || "Error occured" }</h1> }

      { isFetched && data?.contacts && (
        <List>
          {data?.contacts.map(({ _id, firstName, phoneNumber, lastName }) => (
            <Fragment key={_id}>
              <ListItem>
                <ListItemText>
                  {firstName} {lastName} - {phoneNumber}
                </ListItemText>
                <Button
                  variant="contained"
                  color="primary"
                  size="large"
                  type="submit"
                  onClick={() => {
                    setContactId(_id)
                  }}
                >Edit</Button>
                <Button
                  variant="contained"
                  type="submit"
                >Delete</Button>
              </ListItem>
              <Divider component="li" />
            </Fragment>
          ))}
        </List>
      )}

    </Paper>
  );
}

Contacts.propTypes = {
  classes: PropTypes.object.isRequired
}


export default withStyles(styles)(Contacts);
