import { Grid, Paper } from "@mui/material";
import { withStyles } from "@mui/styles";
import PropTypes from "prop-types";
import { useQuery } from "react-query";
import ContactForm from "./ContactForm";


const styles ={
  paper: {
    margin: "24px",
    padding: "16px" 
  }
}

const Contacts = ({ classes }) => {
  const { isFetching, isFetched, error, data: { contacts = null, message }} = useQuery("/contacts");

  return (
    <Grid container>
      <Grid item xs={5}>
        <Paper className={classes.paper}>
          <ContactForm />
        </Paper>
      </Grid>
      <Grid item xs={7}>
        <Paper className={classes.paper}>
          <div>List of contacts</div>
          {isFetching && <h1>Loading....</h1>}

          {isFetched && error && <h1>{message || "Error occured"}</h1>}

          {isFetched && contacts && (
            <ul>
              {contacts.map(({ firstName, phoneNumber }) => (
                <li>{firstName} - {phoneNumber}</li>
              ))}
            </ul>
          )}
        </Paper>
      </Grid>
    </Grid>
  );
}

Contacts.propTypes = {
  classes: PropTypes.object.isRequired
}

export default withStyles(styles)(Contacts);
