import { ACTION_TYPES } from "../actions/contacts";

const initialState = {
    list:[]
}
export const contacts = (state=initialState, action) => {
    switch (action.type) {
        case ACTION_TYPES.FETCH_ALL:
            return {
                ...state,
                list: [action.payload]
            }

        default:
            return state;
    }
}
