import ContactForm from "./ContactForm";


const styles ={
  paper: {
    margin: "24px",
    padding: "16px" 
  }
}

const Contacts = ({ classes }) => {
  const contact = useQuery("/contacts");
  const { isFetching, isFetched, error, data } = contact;

  // const  { contacts, message } = data;

  return (
    <Grid container>
      <Grid item xs={5}>
