import { api } from "api.js";

export const ACTION_TYPES = {
    CREATE : "CREATE",
    UPDATE : "UPDATE",
    DELETE : "DELETE",
    FETCH_ALL : "FETCH_ALL"
}

export const fetchAll = () => {
    return(dispatch => {
        api.postMessage().fetchAll()
        .then()
        .catch(err => console.error(err.message))
        dispatch({
            type: ACTION_TYPES.FETCH_ALL,
            payload: []
        })
    })
}
