import { Grid, Paper, withStyles } from "@mui/material";
import { margin } from "@mui/system";
import React, {useEffect, useState} from "react";
import { connect } from "react-redux";
import * as actions from "../actions/contacts";
import ContactForm from "./ContactForm";


const styles = theme => ({
  paper: {
    margin: theme.spacing(3),
    padding: theme.spacing(2)
  }
})


const Contacts = ({classes, ...props}) => {

  useEffect(() => {
    props.fetchAllContacts()
  }, [])

  return (
    <Grid container>
      <Grid item xs={5}>
        <Paper classes={classes.paper}>
          <ContactForm />
        </Paper>
      </Grid>
      <Grid item xs={7}>
        <Paper classes={classes.paper}>
          <div>List of contacts</div>
        </Paper>
      </Grid>
    </Grid>
  );
}

const mapStateToProps = state => ({
  contactList : state.contacts.list
})

const mapActionToProps = {
  fetchAllContacts : actions.fetchAll
}

export default connect(mapStateToProps, mapActionToProps)(withStyles(styles) (Contacts));
