import { ACTION_TYPES } from "../actions/createContact";

const initialState = {
    list:[]
}
export const createContact = (state, action) => {
    switch (action.type) {
        case ACTION_TYPES.FETCH_ALL:
            return {
                ...state,
                list: [...action.payload]
            }

        default:
            return state;
    }
}
