import React, {useState, useEffect} from "react";

const UseForm = (initialFieldValues) => {

    const [values, setValues] = useState(initialFieldValues);

    const handleInputChange = e => {
        const {field, value} = e.target
        setValues({
        ...value,
        [field]: value
        })
    }

    return {
        values,
        setValues,
        handleInputChange
    };
}

export default UseForm;
