import { Button, TextField } from "@mui/material";
import { withStyles } from "@mui/styles";
import PropTypes from "prop-types";
import React from "react";
import { Formik, ErrorMessage } from "formik";
import * as Yup from "yup";
import { useQuery } from "react-query";

const styles ={
  root: {
    '& .MuiTextField-root': { margin: "8px", },
  },
  form: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center"
  },
  submitButton: {
    width: "50%"
  }
}

const ContactForm = ({ classes, contactId }) => {
  const contact = useQuery(`/contacts/${contactId}`)
  const handleSubmit = (values, actions) => {
    console.log(values);
    actions.setSubmitting(false);
  }


  return (
    <Formik
      initialValues={{
        firstName: "",
        lastName: "",
        email: "",
        phoneNumber: "",
      }}
      validationSchema={Yup.object({
        firstName: Yup.string().required(),
        lastName: Yup.string().required(),
        email: Yup.string().email().required(),
        phoneNumber: Yup.string().min(10).max(16).required(),
      })}
      onSubmit={handleSubmit}
    >
      {({ handleSubmit, handleChange, handleBlur, values }) => (
        <form className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit} >
          <TextField
            name="firstName"
            variant="outlined"
            label="First Name"
            fullWidth
            value={values.firstName}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          <ErrorMessage name="firstName" component="p" />

          <TextField
            name="lastName"
            variant="outlined"
            label="Last Name"
            fullWidth
            value={values.lastName}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          <ErrorMessage name="lastName" component="p" />

          <TextField
            name="email"
            variant="outlined"
            label="Email"
            fullWidth
            value={values.email}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          <ErrorMessage name="email" component="p" />

          <TextField
            name="phoneNumber"
            variant="outlined"
            label="Phone Number"
            fullWidth
            value={values.phoneNumber}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          <ErrorMessage name="phoneNumber" component="p" />

          <Button
            variant="contained"
            color="primary"
            size="large"
            type="submit"
            className={classes.submitButton}
          >Submit</Button>
        </form>
      )}
    </Formik>
  );
}

ContactForm.propTypes = {
  classes: PropTypes.object.isRequired
}

export default withStyles(styles)(ContactForm);
