import { Paper, List, ListItem, ListItemText, Divider, Button,  } from "@mui/material";
import { withStyles } from "@mui/styles";
import PropTypes from "prop-types";
import { Fragment } from "react";
import { useQuery } from "react-query";
import { useStore } from "../store";
import DeleteModal from "./DeleteModal";


const styles ={
  paper: {
    margin: "24px",
    padding: "16px" 
  }
}

const Contacts = ({ classes }) => {
  const contact = useQuery("/contacts");
  const store = useStore();
  const { isFetching, isFetched, error, data } = contact;

  return (
    <Paper className={classes.paper}>
      <div>List of contacts</div>

      { isFetching && <h1>Loading...</h1> }

      { isFetched && error && <h1>{data?.message || "Error occured" }</h1> }

      { isFetched && data?.contacts && (
        <List>
          {data?.contacts.map(({ _id, firstName, phoneNumber, lastName, email }) => (
            <Fragment key={_id}>
              <ListItem>
                <ListItemText>                  
                  {firstName} {lastName} - {phoneNumber}
                </ListItemText>
                <Grid item xs container direction="column" spacing={2}>
                  <Grid item xs>
                    <Typography gutterBottom variant="subtitle1" component="div">
                      Standard license
                    </Typography>
                    <Typography variant="body2" gutterBottom>
                      Full resolution 1920x1080 • JPEG
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      ID: 1030114
                    </Typography>
                  </Grid>
                  <Grid item>
                    <Typography sx={{ cursor: 'pointer' }} variant="body2">
                      Remove
                    </Typography>
                  </Grid>
                </Grid>

                <Button
                  variant="contained"
                  color="primary"
                  size="large"
                  type="submit"
                  onClick={() => {
                    store.updateStore("contact", { editingId: _id });
                    store.updateStore("formModal", { open: true })
                  }}
                >
                  Edit
                </Button>             
              </ListItem> 
              <DeleteModal/>             
              <Divider component="li" />

            </Fragment>
          ))}
        </List>
      )}

    </Paper>
  );
}

Contacts.propTypes = {
  classes: PropTypes.object.isRequired
}


export default withStyles(styles)(Contacts);
