import axios from "axios";

const baseUrl = "http://localhost:5050/api/v1/contacts/"

export default {
    createContact(url = baseUrl)
    {
        return{
            fetchAll: () => axios.get(url+"all/"),
            fetchById : id => axios.get(url+id),
            create : newContact => axios.post(url, newContact),
            update: (id, recordToUpdate) => axios.put(url+id, recordToUpdate),
            delete: id => axios.delete(url+id)
        }
    }
}
