import { Button, TextField } from "@mui/material";
import { withStyles } from "@mui/styles";
import PropTypes from "prop-types";
import React, { useCallback, useMemo } from "react";
import { Formik, ErrorMessage } from "formik";
import * as Yup from "yup";
import { useMutation, useQuery } from "react-query";
import { useStore } from "../store";

const styles ={
  root: {
    '& .MuiTextField-root': { margin: "8px", },
  },
  form: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center"
  },
  submitButton: {
    width: "50%"
  }
}

const ContactForm = ({ classes }) => {
  const mutation = useMutation("/contacts");

  const { updatecontact: {
    editingId: contactId
  }} = useStore();

  const { data } = useQuery(`/contacts/${contactId}`, {
    enabled: Boolean(contactId),
  });

  const handleCreateContact = (values, actions) => {
    try {
      mutation.mutate(values);
    } finally {
      actions.setSubmitting(false);
    }
  };

  const handleUpdateContact = useCallback((values, actions) => {
    console.log("Update contact", contactId, values)
  }, [contactId])

  const initialValues = useMemo(() => {
    return [
      "firstName",
      "lastName",
      "email",
      "phoneNumber",
    ].reduce((values, key) => ({
      ...values,
      [key]: data?.contact?.[key] ?? "",
    }), {})
  }, [data?.contact]);

  return (
    <Formik
      enableReinitialize
      onSubmit={
        !Boolean(data?.contact)
          ? handleCreateContact
          : handleUpdateContact
      }
      initialValues={initialValues}
      validationSchema={Yup.object({
        firstName: Yup.string().required(),
        lastName: Yup.string().required(),
        email: Yup.string().email().required(),
        phoneNumber: Yup.string().min(10).max(16).required(),
      })}
    >
      {({ handleSubmit, handleChange, handleBlur, handleReset, values, isSubmitting }) => (
        <form className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit} >
          <TextField
            name="firstName"
            variant="outlined"
            label="First Name"
            fullWidth
            value={values.firstName}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          <ErrorMessage name="firstName" component="p" />

          <TextField
            name="lastName"
            variant="outlined"
            label="Last Name"
            fullWidth
            value={values.lastName}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          <ErrorMessage name="lastName" component="p" />

          <TextField
            name="email"
            variant="outlined"
            label="Email"
            fullWidth
            value={values.email}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          <ErrorMessage name="email" component="p" />

          <TextField
            name="phoneNumber"
            variant="outlined"
            label="Phone Number"
            fullWidth
            value={values.phoneNumber}
            onChange={handleChange}
            onBlur={handleBlur}
          />
          <ErrorMessage name="phoneNumber" component="p" />

          <Button
            variant="contained"
            color="primary"
            size="large"
            type="submit"
            className={classes.submitButton}
            disabled={isSubmitting}
          >
            {!Boolean(data?.contact) ? "Create" : "Update" }
          </Button>

          {Boolean(data?.contact) && (
            <Button
              variant="contained"
              color="primary"
              size="large"
              type="reset"
              onClick={() => {
                handleReset();
                setContactId(null);
              }}
              className={classes.submitButton}
            >
              Reset
            </Button>
          )}

        </form>
      )}
    </Formik>
  );
}

ContactForm.propTypes = {
  classes: PropTypes.object.isRequired
}

export default withStyles(styles)(ContactForm);
