import {useState} from "react";
import { Routes, Route, Link,} from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";

import ViewContacts from "./components/contact-list";
import AddContact from "./components/create-contacts";
import DeleteContact from "./components/delete-contact";
import UpdateContact from "./components/update-contact";


function App() {
  const [contact, setContact] = useState(null)

  return (
    <div >
      <nav className="navbar navbar-expand navbar-dark bg-dark">
        <a href="/view-contacts" className="navbar-brand">
          Contacts
        </a>
        <div className="navbar-nav mr-auto">
          <li className="nav-item">
            <Link to={"/create-contact"} className="nav-link">
              Create Contact
            </Link>
          </li>
          <li className="nav-item">
            <Link to={"/view-contacts"} className="nav-link">
              View Contacts
            </Link>
          </li>
          <li className="nav-item">
            <Link to={"/update-contact"} className="nav-link">
              Update Contact
            </Link>
          </li>
          <li className="nav-item">
            <Link to={"/delete-contact"} className="nav-link">
              Delete Contact
            </Link>
          </li>
        </div>
      </nav>

      <div>
        <Routes>
          <Route exact path="/view-contacts" component={ViewContacts}/>
          <Route 
            path="/create-contact" 
            render={() => (
              <AddContact />
            )}
          />
          <Route
            path="/update-contact/:id"
            render={(props) => (
              <UpdateContact {...props} contact={contact}/>
            )}
          />
          <Route
            path="/delete-contact/:id"
            render={(props) => (
              <DeleteContact {...props} contact={contact}/>
            )}
          />
        </Routes>
      </div>
    </div>
  );
}

export default App;
